CREATE TABLE ALBARANES (ID_ALBARAN SMALLINT NOT NULL,ID_CLIENTE SMALLINT NOT NULL,ID_VEHICULO SMALLINT NOT NULL,FECHA VARCHAR(255),RUTA VARCHAR(1000),CONSTRAINT PK_ALBARANES PRIMARY KEY (ID_ALBARAN,ID_CLIENTE,ID_VEHICULO))
CREATE TABLE AUTO_INCREMENT (ID_CLIENTE INTEGER NOT NULL WITH DEFAULT 0,ID_VEHICULO INTEGER NOT NULL WITH DEFAULT 0,ID_PRESUPUESTO INTEGER NOT NULL WITH DEFAULT 0,ID_CARGO INTEGER NOT NULL WITH DEFAULT 0,ID_NOTA INTEGER NOT NULL WITH DEFAULT 0)
CREATE TABLE CARGO_DOCUMENTO (ID_DOCUMENTO INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,ID_CARGO INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,ID_EMPRESA INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,TIPO VARCHAR(255) NOT NULL,SERIE INTEGER NOT NULL WITH DEFAULT 0 NOT NULL, REFERENCIA VARCHAR(50),DESCRIPCION VARCHAR(255),UNIDAD DOUBLE NOT NULL WITH DEFAULT 0,PVP DOUBLE NOT NULL WITH DEFAULT 0,DTO INTEGER NOT NULL WITH DEFAULT 0,TOTAL DOUBLE NOT NULL WITH DEFAULT 0,CONSTRAINT PK_CARGO_DOCUMENTO PRIMARY KEY (ID_DOCUMENTO,ID_CARGO,ID_EMPRESA,TIPO,SERIE))
CREATE TABLE CLIENTES (ID_CLIENTE INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,IDFISCAL VARCHAR(255),NOMBRE VARCHAR(255),DIRECCION VARCHAR(255),LOCALIDAD VARCHAR(255),PROVINCIA VARCHAR(255),TELEFONO INTEGER NOT NULL WITH DEFAULT 0,MOVIL INTEGER NOT NULL WITH DEFAULT 0,CODIGO_POSTAL INTEGER NOT NULL WITH DEFAULT 0,PORTAL VARCHAR(255),PISO INTEGER NOT NULL WITH DEFAULT 0,LETRA VARCHAR(255),EMAIL VARCHAR(255),ACTIVO VARCHAR(1) DEFAULT 'S',DEUDA DOUBLE NOT NULL WITH DEFAULT 0 NOT NULL,CONSTRAINT PK_CLIENTES PRIMARY KEY (ID_CLIENTE))
CREATE TABLE COMBUSTIBLES (TIPO_COMBUSTIBLE VARCHAR(255) NOT NULL,CONSTRAINT PK_COMBUSTIBLES PRIMARY KEY (TIPO_COMBUSTIBLE))
CREATE TABLE CP_MUN_PROV (CP VARCHAR(255),LOCALIDAD VARCHAR(255),PROVINCIA VARCHAR(255))
CREATE TABLE DATOSEMPRESA (ID_EMPRESA INTEGER NOT NULL,EMPRESA VARCHAR(255),NOMBRE VARCHAR(255),DIRECCION VARCHAR(255),TELEFONO VARCHAR(255),CIF VARCHAR(255),CODIGOPOSTA VARCHAR(255),MUNICIPIO VARCHAR(255),PROVINCIA VARCHAR(255),ID_FACTURA INTEGER NOT NULL WITH DEFAULT 0,PREDEFINIDA BOOLEAN NOT NULL WITH DEFAULT FALSE,CONSTRAINT PK_DATOSEMPRESA PRIMARY KEY (ID_EMPRESA))
CREATE TABLE DOCUMENTO (ID_CLIENTE INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,ID_VEHICULO INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,ID_DOCUMENTO INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,ID_EMPRESA INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,TIPO VARCHAR(2) NOT NULL,SERIE INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,IMPUESTO INTEGER,FECHA VARCHAR(100),KM_VEHICULO INTEGER NOT NULL WITH DEFAULT 0,TOTA_PRESUPUESTO VARCHAR(2),FORMAPAGO VARCHAR(50),CONSTRAINT PK_DOCUMENTO PRIMARY KEY (ID_CLIENTE,ID_VEHICULO,ID_DOCUMENTO,ID_EMPRESA,TIPO,SERIE))
CREATE TABLE GENERICOS (ID_GENERICO INTEGER NOT NULL,NOMBRE_GENERICO VARCHAR(255),DESCRIPCION VARCHAR(255),UNIDADES DOUBLE,PVP DOUBLE,DTO SMALLINT,TOTAL DOUBLE,CONSTRAINT PK_GENERICOS PRIMARY KEY (ID_GENERICO))
CREATE TABLE MARCA (ID_MARCA INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,NOMBRE VARCHAR(255),CONSTRAINT PK_MARCA PRIMARY KEY (ID_MARCA))
CREATE TABLE MODELO (ID_MARCA INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,ID_MODELO INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,NOMBRE VARCHAR(255),CONSTRAINT PK_MODELO PRIMARY KEY (ID_MARCA,ID_MODELO))
CREATE TABLE NOTAS (ID_CLIENTE INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,ID_NOTA INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,NOTA VARCHAR(4000),NOMBRE VARCHAR(255),IMPORTANTE BOOLEAN NOT NULL WITH DEFAULT false,PRINCIPAL BOOLEAN,CONSTRAINT PK_NOTAS PRIMARY KEY (ID_CLIENTE,ID_NOTA))
CREATE TABLE PARAMETROS (NOMBRE_PARAMETRO VARCHAR(255) NOT NULL,VALOR_PARAMETRO VARCHAR(4000),CONSTRAINT PK_PARAMETROS PRIMARY KEY (NOMBRE_PARAMETRO))
CREATE TABLE VEHICULOS (ID_CLIENTE INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,ID_VEHICULO INTEGER NOT NULL WITH DEFAULT 0 NOT NULL,MATRICULA VARCHAR(255),MARCA VARCHAR(255),MODELO VARCHAR(255),COMBUSTIBLE VARCHAR(255),FICHA_TECNICA VARCHAR(500),BASTIDOR VARCHAR(17),ACTIVO VARCHAR(1) DEFAULT 'S',CONSTRAINT PK_VEHICULOS PRIMARY KEY (ID_CLIENTE,ID_VEHICULO))